import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { bindActionCreators } from "redux";
import { actionCreators } from "../../state";

export default function PlayerTable() {
  const state = useSelector((state) => state.playerList);
  const playerList = [...state];
  const dispatch = useDispatch();
  const { sortPlayerListByStat } = bindActionCreators(actionCreators, dispatch);

  const [activeStat, setActiveStat] = useState("");
  const [columnDirection, setColumnDirection] = useState(true);

  const handleStatSort = (stat, ascending) => {
    setActiveStat(stat);
    let metric = {
      stat,
      ascending,
    };
    sortPlayerListByStat(metric);
  };

  return (
    <>
      {playerList.length > 0 && (
        <div className="overflow-auto text-center">
          <div className="py-2">
            <div className=" uppercase text-clear text-center">
              Season Averages
            </div>
          </div>
          <table className=" border-collapse w-full table-fixed break-word hover:table-fixed">
            <thead>
              <tr className="h-12">
                <th className="uppercase bg-gray-300 text-gray-600 border border-gray-300  lg:table-cell md:w-24 w-24">
                  Name
                </th>
                <th className="uppercase bg-gray-300 text-gray-600 border border-gray-300  lg:table-cell md:w-24 w-24">
                  Season
                </th>
                <th className="uppercase bg-gray-300 text-gray-600 border border-gray-300  lg:table-cell md:w-24 w-24">
                  Min
                </th>
                <th
                  className={
                    activeStat === "games_played"
                      ? "uppercase bg-cyan text-black border border-gray-600  w-15 cursor-pointer"
                      : "uppercase bg-gray-200 text-gray-600 border border-gray-300 w-10 cursor-pointer hover:bg-deepcyan hover:text-blue"
                  }
                  onClick={() =>
                    handleStatSort("games_played", columnDirection)
                  }
                >
                  GP
                </th>
                <th
                  className={
                    activeStat === "pts"
                    ? "uppercase bg-cyan text-black border border-gray-600  w-15 cursor-pointer"
                    : "uppercase bg-gray-200 text-gray-600 border border-gray-300 w-10 cursor-pointer hover:bg-deepcyan hover:text-blue"
                  }
                  onClick={() => handleStatSort("pts", columnDirection)}
                >
                  PTS
                </th>
                <th
                  className={
                    activeStat === "fg_pct"
                    ? "uppercase bg-cyan text-black border border-gray-600  w-15 cursor-pointer"
                    : "uppercase bg-gray-200 text-gray-600 border border-gray-300 w-10 cursor-pointer hover:bg-deepcyan hover:text-blue"
                  }
                  onClick={() => handleStatSort("fg_pct", columnDirection)}
                >
                  FG%
                </th>
                <th
                  className={
                    activeStat === "fga"
                    ? "uppercase bg-cyan text-black border border-gray-600  w-15 cursor-pointer"
                      : "uppercase bg-gray-200 text-gray-600 border border-gray-300 w-10 cursor-pointer hover:bg-deepcyan hover:text-blue"
                  }
                  onClick={() => handleStatSort("fga", columnDirection)}
                >
